export class CompilerBuffer {
    #content = '';
    newLine = '\n';
    writeStatement(statement) {
        this.#content = `${this.#content}${this.newLine}${statement}`;
    }
    child() {
        return new CompilerBuffer();
    }
    toString() {
        return this.#content;
    }
    flush() {
        this.#content = '';
    }
}
