export function createRootField(parent) {
    return {
        parentValueExpression: parent.variableName,
        fieldNameExpression: `''`,
        fieldPathExpression: `''`,
        wildCardPath: '',
        variableName: `${parent.variableName}_item`,
        valueExpression: 'root',
        outputExpression: parent.outputExpression,
        isArrayMember: false,
    };
}
