export function defineFieldNullOutput({ allowNull, conditional, variableName, outputExpression, transformFnRefId, }) {
    if (!allowNull) {
        return '';
    }
    return `${conditional || 'if'}(${variableName}.value === null) {
  ${outputExpression} = ${transformFnRefId ? `refs['${transformFnRefId}'](null, ${variableName});` : 'null;'}
}`;
}
