export function defineFieldVariables({ parseFnRefId, variableName, wildCardPath, isArrayMember, valueExpression, fieldNameExpression, parentValueExpression, }) {
    const inValueExpression = parseFnRefId
        ? `refs['${parseFnRefId}'](${valueExpression})`
        : valueExpression;
    return `const ${variableName} = defineValue(${inValueExpression}, {
  data: root,
  meta: meta,
  name: ${fieldNameExpression},
  wildCardPath: '${wildCardPath}',
  mutate: defineValue,
  report: report,
  isValid: true,
  parent: ${parentValueExpression},
  isArrayMember: ${isArrayMember},
});`;
}
